import './App.css';
import { Component } from 'react';
import Key from './Key'

class App extends Component {

    state = {

        keys: [
            {value: 'A', state: 'none'},
            {value: 'B', state: 'none'}
        ]
    }

    //allKeys = [...'ABCDEFGHIJKLMNOPQRSTUVWXYZ']

    handleKeyClick(key) {

        const { keys } = this.state


        const updatedKeys = [...keys]
        const index = updatedKeys.findIndex(e => e.value === key )
        updatedKeys[index].state = 'matched'

        this.setState({ keys: updatedKeys })

        console.log(key, index, keys)
    }

    render() {

        return (
            <div className="App">
                <button onClick={() => console.log(this.state)}>Check state</button>

                <ul>
                    {this.state.keys.map(k => {
                        return <Key 
                            key={k.value} 
                            keyValue={k.value} 
                            state={'none'}
                            onClick={(value) => {this.handleKeyClick(value)}}
                            />
                    })}
                </ul>
            </div>
        );
    }

}

export default App;
