import './App.css';
import { Component } from 'react';
import Key from './Key'

class App extends Component {

    state = {

        keys: [{value: 'A', state: 'none'}]
    }

    //allKeys = [...'ABCDEFGHIJKLMNOPQRSTUVWXYZ']

    handleKeyClick(key) {

        const { keys } = this.state

        const index = keys.findIndex(e => e.value == key )

        console.log(key, index, keys)
    }

    render() {

        return (
            <div className="App">
                <ul>
                    {this.state.keys.map(k => {
                        return <Key 
                            key={k.value} 
                            keyValue={k.value} 
                            state={'none'}
                            onClick={(value) => {this.handleKeyClick(value)}}
                            />
                    })}
                </ul>
            </div>
        );
    }

}

export default App;
