import './App.css';
import { Component } from 'react';
import Key from './Key'
import Word from './Word'
import Win from './Win';

class App extends Component {

    INITIALE_STATE = {

        keys: [...'ABCDEFGHIJKLMNOPQRSTUVWXYZ'].map(l => {return {value: l, state: 'none'}}),

        currentWord: 'MAISONS',
        lettersFound: []
    }

    // Deep clone of INITIAL_START
    // cause the spread copy make a shallow copy
    // and note a "hard copy"
    state = JSON.parse(JSON.stringify(this.INITIALE_STATE))

    handleKeyClick(key) {

        // Create copy of state variable
        // that I'll change in this function
        const keys = [...this.state.keys]
        const lettersFound = this.state.lettersFound

        // Perform
        const index = keys.findIndex(e => e.value === key )

        keys[index].state = 'mismatched'
        for(const letter of [...this.state.currentWord]) {

            if(letter === key) {

                keys[index].state = 'matched'
                lettersFound.push(key)
            }
        }

        // Update state of this compenent 
        // with the new performed values
        this.setState({ keys, lettersFound })
    }

    handleRestart() {

        const { keys, currentWord, lettersFound} = this.INITIALE_STATE

        this.setState({ keys, currentWord, lettersFound })
    }

    render() {

        return (
            <div className="App">
                <button onClick={() => console.log(this.state)}>DEBUG : Check state</button>
                <br /><br />
                <div>
                    <Word 
                        lettersFound={this.state.lettersFound} 
                        currentWord={this.state.currentWord} 
                        />
                </div>

                <ul>
                    {this.state.keys.map(k => {
                        return <Key 
                            key={k.value} 
                            keyValue={k.value} 
                            state={k.state}
                            onClick={(value) => {this.handleKeyClick(value)}}
                            />
                    })}
                </ul>

                <Win 
                    won={true}
                    onRestart={() => {this.handleRestart()}}
                    />
            </div>
        );
    }

}

export default App;
